import Image from 'next/image'

interface CachedImageProps {
  src: string
  alt: string
  width: number
  height: number
  className?: string
  priority?: boolean
  loading: string
}

export function CachedImage({
  src,
  alt,
  width,
  height,
  className,
  priority = true,
  loading = "eager"
}: CachedImageProps) {
  return (
    <Image
      src={src}
      alt={alt}
      width={width}
      height={height}
      className={className}
      priority={priority}
      loading={loading as "eager" | "lazy"}
      quality={85}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
    />
  )
}